import React, { useState } from "react";
import './Contact.css'
import styled from 'styled-components'


const nameInput = document.querySelector('.name')
const emailInput = document.querySelector('.email')
const subjectInput = document.querySelector('.subject')
const textareaInput = document.querySelector('.textarea')

const contactForm = document.querySelector('.contact-form')

contactForm.addEventListener('submit', (evt) => {
  evt.preventDefault()
  validateInput()
})

const validateInput = () => {
  let email = emailInput.value
  let textarea = textareaInput.value

  if (!email && !textarea) {
    setError(emailInput.parentElement)
    setError(textareaInput.parentElement)
    showMessage('Please fill in the required inputs')
  } else if (!email && textarea) {
    setError(emailInput.parentElement)
    showMessage("Oops Email can't be empty")
  } else if (!textarea && email) {
    setError(textareaInput.parentElement)
    showMessage('Please provide a message')
  } else if (email && textarea) {
    emailjs.sendForm(
      'service_rvlqach',
      'template_d32ix5s',
      contactForm,
      'lD25U1N6WN3XbKSFV',
    )
    setSuccess(emailInput.parentElement)
    setSuccess(textareaInput.parentElement)
    showMessage('Message sent successfully', 'green')
    textareaInput.value = ''
    emailInput.value = ''
    nameInput.value = ''
    subjectInput.value = ''
  }
}

const setError = (input) => {
  if (input.classList.contains('success')) {
    input.classList.remove('success')
  } else {
    input.classList.add('error')
  }
}
const setSuccess = (input) => {
  if (input.classList.contains('error')) {
    input.classList.remove('error')
  } else {
    input.classList.add('success')
  }
}

const messageDiv = document.querySelector('.message')
const showMessage = (message, updateColor) => {
  const divContent = document.createElement('div')
  divContent.textContent = message
  divContent.classList.add('message-content')
  divContent.style.backgroundColor = updateColor
  messageDiv.prepend(divContent)

  messageDiv.style.transform = `translate(${(0, 0)}%)`
  setTimeout(() => {
    divContent.classList.add('hide')
    divContent.addEventListener('transitionend', () => {
      divContent.remove()
    })
  }, 5000)
}

const ContactContainer = styled('div')({
    backgroundColor: '#1d1d1d',
})

const Form = styled('form')({
    maxWidth: '500px',
	padding: '10px 20px',
	background: '#1d1d1d',
	margin:' 10px auto',
	padding: '20px',
	borderRadius: '8px',
	fontFamily:' Georgia, "Times New Roman", Times, serif',
})

const Input = styled('input')({
    position: 'relative',
	display: 'block',
	padding: '19px 39px 18px 39px',
	color: '#8d8d8d',
	margin:' 0 auto',
	background: '#2b2b2b',
	fontSize: '18px',
	textAlign: 'center',
	fontstyle: 'normal',
	width: '100%',
	border: '1px solid #16a085',
	borderWidth: '1px 1px 3px',
	marginBottom: '10px',
})

const TextArea = styled('textarea')({
    position: 'relative',
	display: 'block',
	padding: '19px 39px 18px 39px',
	color: '#8d8d8d',
	margin:' 0 auto',
	background: '#2b2b2b',
	fontSize: '18px',
	textAlign: 'center',
	fontstyle: 'normal',
	width: '100%',
	border: '1px solid #16a085',
	borderWidth: '1px 1px 3px',
	marginBottom: '10px',
})

const Contact = () => {
  const [submitted, setSubmitted] = useState(false);
  const handleSubmit = () => {
    setTimeout(() => {
      setSubmitted(true);
    }, 100);
  };

  if (submitted) {
    return (
      <>
        <div className="thanksmsg" style={{color: 'white'}}>Thank you!</div>
        <div className="beInTouch" style={{color: 'white'}}>I'll be in touch soon.</div>
      </>
    );
  }

  return (
      <ContactContainer>
    <Form
    //   action={FORM_ENDPOINT}
      onSubmit={handleSubmit}
    //   method="POST"
    //   target="_blank"
    >
        <h1 style={{color: 'white'}}>Let's get in touch!</h1>
      <div className="name">
        <Input
          type="text"
          placeholder="Your name"
          name="name"
          required
        />
      </div>
      <div className="email" >
        <Input
          type="email"
          placeholder="Email"
          name="email"
          required

        />
      </div>
      <div className="message">
      <p style={{marginTop: '85px'}}>
        <TextArea
          placeholder="Your message"
          name="message"
          required
        />
        </p>
      </div>
      <div className="submit">
        <button
          type="submit"
        //   style={{
        //   height: '50px', 
        //   width: '100%', 
        //   color: 'transparent',
        //   position: 'relative',
        //   alignItems: 'center',
        //   textDecoration: 'none',
        //   letterSpacing: '2px',
        //   webkitTextStroke: '1px rgba(255, 255, 255, .5)',
        //   fontSize: '2em',
        //   textTransform: 'uppercase',
        //   lineHeight: '1em',
        //   backgroundColor: 'transparent',
        // }}
        >
          Send a message
        </button>
      </div>
    </Form>
    </ContactContainer>
  );
};

export default Contact;
