import React from 'react';
import ReactDOM from 'react-dom';
import App from './App';
import Skills from './components/pages/Skills';
import emailjs from 'emailjs/browser'
import styled from 'styled-components';
imp

const nameInput = document.querySelector('.name')
const emailInput = document.querySelector('.email')
const subjectInput = document.querySelector('.subject')
const textareaInput = document.querySelector('.textarea')

const contactForm = document.querySelector('.contact-form')

contactForm.addEventListener('submit', (evt) => {
  evt.preventDefault()
  validateInput()
})

const validateInput = () => {
  let email = emailInput.value
  let textarea = textareaInput.value

  if (!email && !textarea) {
    setError(emailInput.parentElement)
    setError(textareaInput.parentElement)
    showMessage('Please fill in the required inputs')
  } else if (!email && textarea) {
    setError(emailInput.parentElement)
    showMessage("Oops Email can't be empty")
  } else if (!textarea && email) {
    setError(textareaInput.parentElement)
    showMessage('Please provide a message')
  } else if (email && textarea) {
    emailjs.sendForm(
      'service_rvlqach',
      'template_d32ix5s',
      contactForm,
      'lD25U1N6WN3XbKSFV',
    )
    setSuccess(emailInput.parentElement)
    setSuccess(textareaInput.parentElement)
    showMessage('Message sent successfully', 'green')
    textareaInput.value = ''
    emailInput.value = ''
    nameInput.value = ''
    subjectInput.value = ''
  }
}

const setError = (input) => {
  if (input.classList.contains('success')) {
    input.classList.remove('success')
  } else {
    input.classList.add('error')
  }
}
const setSuccess = (input) => {
  if (input.classList.contains('error')) {
    input.classList.remove('error')
  } else {
    input.classList.add('success')
  }
}

const messageDiv = document.querySelector('.message')
const showMessage = (message, updateColor) => {
  const divContent = document.createElement('div')
  divContent.textContent = message
  divContent.classList.add('message-content')
  divContent.style.backgroundColor = updateColor
  messageDiv.prepend(divContent)

  messageDiv.style.transform = `translate(${(0, 0)}%)`
  setTimeout(() => {
    divContent.classList.add('hide')
    divContent.addEventListener('transitionend', () => {
      divContent.remove()
    })
  }, 5000)
}

const ContactContainer = styled('div')({
    backgroundColor: '#1d1d1d',
})

const Form = styled('form')({
    maxWidth: '500px',
	padding: '10px 20px',
	background: '#1d1d1d',
	margin:' 10px auto',
	padding: '20px',
	borderRadius: '8px',
	fontFamily:' Georgia, "Times New Roman", Times, serif',
})

const Input = styled('input')({
    position: 'relative',
	display: 'block',
	padding: '19px 39px 18px 39px',
	color: '#8d8d8d',
	margin:' 0 auto',
	background: '#2b2b2b',
	fontSize: '18px',
	textAlign: 'center',
	fontstyle: 'normal',
	width: '100%',
	border: '1px solid #16a085',
	borderWidth: '1px 1px 3px',
	marginBottom: '10px',
})

const TextArea = styled('textarea')({
    position: 'relative',
	display: 'block',
	padding: '19px 39px 18px 39px',
	color: '#8d8d8d',
	margin:' 0 auto',
	background: '#2b2b2b',
	fontSize: '18px',
	textAlign: 'center',
	fontstyle: 'normal',
	width: '100%',
	border: '1px solid #16a085',
	borderWidth: '1px 1px 3px',
	marginBottom: '10px',
})

const Contact = () => {
  const [submitted, setSubmitted] = useState(false);
  const handleSubmit = () => {
    setTimeout(() => {
      setSubmitted(true);
    }, 100);
  };

  if (submitted) {
    return (
      <>
        <div className="thanksmsg" style={{color: 'white'}}>Thank you!</div>
        <div className="beInTouch" style={{color: 'white'}}>I'll be in touch soon.</div>
      </>
    );
  }

  return (

     <div class="contact-section contact-wrapper">
      <div class="fake-big">@</div>
      <form class="contact-form">
        <div class="contact-heading">
          <div id="header">
            <h2>Contact me</h2>
          </div>
          <p>
            I’m interested in freelance opportunities – especially ambitious or
            large projects. However, if you have other request or question,
            don’t hesitate to use the form.
          </p>
        </div>

        <div class="form-input-wrapper">
          <div class="form-input-group">
            <input type="text" placeholder="Name" name="name" class="name" />
            <span></span>
          </div>
          <div class="form-input-group">
            <input
              type="email"
              name="email"
              placeholder="Email"
              class="email"
            />
            <span></span>
          </div>
        </div>
        <div class="form-input-group">
          <input
            type="text"
            placeholder="Subject"
            class="subject"
            name="subject"
          />
          <span></span>
        </div>
        <div class="form-input-group">
          <textarea
            type="text"
            placeholder="Message"
            class="textarea"
            name="message"
          ></textarea>
          <span></span>
        </div>
        <div class="message"></div>
        <button type="submit" class="contact-button submit-button">
          <div>
            <span class="bg"></span>
            <span class="base"></span>
            <span class="text">
              Send Message
            </span>
          </div>
        </button>
      </form>
    <div/>
    </div>
  );
};
